mapper.delete:
DELETE FROM dish
WHERE id IN

    #{id}

mapper.select:
SELECT d.*, c.name AS category_name FROM dish d LEFT OUTER JOIN category c ON d.category_id = c.id
AND LOWER(d.name) like concat('%', LOWER(#{name}), '%')
AND d.category_id = #{categoryId}
AND d.status = #{status}
ORDER BY d.create_time DESC

SELECT * FROM dish
WHERE id IN

    #{id}
